mapper.insert:
		
   			SELECT IFNULL(MAX(CONVERT(SUBSTR((admin_no), 6), UNSIGNED)+1), 0) FROM member_admin
   		
		INSERT INTO member_admin (
			 admin_no
			,admin_id
			,admin_pw
			,admin_level
			,admin_team
			,admin_employee_no
			,admin_employee_tell
			,admin_date)
		VALUES (concat("admin", #{admin_no})
			,#{admin_id}
			,#{admin_pw}
			,#{admin_level}
			,#{admin_team}
			,#{admin_employee_no}
			,#{admin_employee_tell}
			,now())

mapper.select:
SELECT count(admin_no) 
  FROM member_admin 
 WHERE admin_id=#{admin_id} 
   AND admin_pw=#{admin_pw}

mapper.update:
    	UPDATE member_admin
    	   SET admin_pw = #{admin_pw}
    		  ,admin_level = #{admin_level}	 
    		  ,admin_team = #{admin_team}
    		  ,admin_employee_no = #{admin_employee_no}
    		  ,admin_employee_tell = #{admin_employee_tell}
		WHERE admin_no = #{admin_id}
